mapper.insert:
   		INSERT 
INTO visaadmininfo( 
    email, firstname, middlename, lastname, deleteflg) 
VALUES ( 
#{email},#{firstname},#{middlename},#{lastname},'ACTIVE');

INSERT INTO visalogintable(email,password,role,deleteflg)VALUES(#{email},#{password},#{role},'ACTIVE');

mapper.select:
SELECT COUNT(*) > 0
FROM visalogintable
WHERE email = #{email};
